mapper.select:
select v.id, v.user_id, v.audio_id, v.video_desc, v.video_path, v.video_seconds, v.video_width, v.video_height, v.cover_path, v.like_counts, status, v.create_time, u.face_image, u.nickname from videos v,users u where v.user_id = u.id
and v.video_desc = #{videoDesc,jdbcType=VARCHAR}
and u.id = #{userId,jdbcType=VARCHAR}
order by v.create_time ,v.like_counts desc


